<script setup>
import { onMounted, reactive } from "vue";
import { useRoute, useRouter } from "vue-router";
const router=useRouter();
const route=useRoute();
let list=[
    {
        id:1,
        name:'Bài 1'
    },
    {
        id:2,
        name:'Bài 2'
    }
]
const post=reactive({
    id:'',
    name:''
})
onMounted(()=>{
   const id=parseInt(route.params.id);
   const postDetail=list.find(p=>p.id==id);
   if(postDetail){
      post.id=postDetail.id;
      post.name=postDetail.name;
   }else{
      router.push('/post-not-found');
   }
    // fetch data from API or other sources to populate the post object with the correct data for the given id
})
</script>

